import { Pipe, PipeTransform } from '@angular/core';
import { CATEGORY_NAME, Categories, EnumCategories } from '~types/product';

@Pipe({
  name: 'categoryName'
})
export class CategoryNamePipe implements PipeTransform {
  transform(category: Categories) {
    return CATEGORY_NAME[EnumCategories[category]];
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'getWord',
})
export class GetWordPipe implements PipeTransform {
  transform(value: string, index: number): string {
    const matches = value.match(/\S+/g);
    const result = matches && matches[index] ? matches[index] : value;
    return result;
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'maxNumber'
})
export class MaxNumberPipe implements PipeTransform {
  transform(value: number, maxValue: number, postFix = ''): string {
    if (value <= maxValue) {
      return `${value}`;
    }
    return `${maxValue}${postFix}`;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { IProduct } from '~types/product';

@Pipe({
