import { Inject, Injectable, PLATFORM_ID } from '@angular/core';
import { BehaviorSubject, Observable, Subject, debounceTime, fromEvent } from 'rxjs';
import { map, takeUntil } from 'rxjs/operators';
import { IS_PLATFORM_BROWSER_TOKEN } from 'src/app/tokens/isPlatformBrowser';
import { WINDOW_TOKEN } from 'src/app/tokens/window';

export interface IViewportSizes {
  width: number;
  height: number;
}

@Injectable({
  providedIn: 'root'
})
export class ViewportSizeService {
  private _destroySubject = new Subject();
  private _resizeSubject: BehaviorSubject<IViewportSizes>;
  private _resizeObservable$ = this.isPlatformBrowser(this.platformId)
    ? fromEvent<UIEvent>(this.window, 'resize').pipe(
        takeUntil(this._destroySubject),
        map(this.getSizesFromResizeEvent),
        debounceTime(200)
      )
    : new Subject<IViewportSizes>();

  constructor(
    @Inject(WINDOW_TOKEN) private window: Window,
    @Inject(PLATFORM_ID) private platformId: string,
    @Inject(IS_PLATFORM_BROWSER_TOKEN) private isPlatformBrowser: (id: string) => boolean
  ) {
    const { innerWidth: width, innerHeight: height } = this.window;
    this._resizeSubject = new BehaviorSubject<IViewportSizes>({
      width,
      height
    });
    this._resizeObservable$.subscribe(this._resizeSubject);
  }

  private getSizesFromResizeEvent(event: UIEvent): IViewportSizes {
    const target = <Window>event.target;

    return {
      width: target.innerWidth,
      height: target.innerHeight
    };
  }

  public get resizeObservable$(): Observable<IViewportSizes> {
    return this._resizeSubject;
  }

  public destroy() {
    this._destroySubject.next(null);
    this._destroySubject.complete();
  }
}

import { Inject, Injectable } from '@angular/core';
import { WINDOW_TOKEN } from 'src/app/tokens/window';

@Injectable({
  providedIn: 'root'
})
export class YmapsService {
  private ymaps: any;
  private maps: any;
  private map: any;

  constructor(@Inject(WINDOW_TOKEN) private window: Window) {}

  public initMap(data: { lat: number; lon: number }[]) {
    return this.loadScriptMap(data).then(() => {
      return this.createMap(data);
    });
  }

  private loadScriptMap(data: { lat: number; lon: number }[]) {
    if (this.window.ymaps) {
      return Promise.resolve();
    }
    const scriptYmaps = document.createElement('script');
    scriptYmaps.src =
      'https://api-maps.yandex.ru/2.1/?apikey=50f8a21f-bac2-4553-bf58-f203bc76de68&lang=ru_RU';
    document.body.appendChild(scriptYmaps);

    return new Promise((resolve, reject) => {
      scriptYmaps.onload = () =>
        this.window.ymaps.ready(() => {
          resolve(true);
        });

      scriptYmaps.onerror = (err) => {
        reject(err);
      };
    });
  }

  private createMap(data: { lat: number; lon: number }[]) {
    if (!this.ymaps) {
      this.ymaps = this.window.ymaps;
    }
    return this.ymaps
      .load()
      .then((maps: any) => {
        this.maps = maps;
        this.map = new maps.Map('address-map', {
          center: [data[0].lat, data[0].lon],
          zoom: 16
        });
      })
      .catch((error: Error) => {
        throw (error.message = 'Failed to load Yandex Maps: ' + error.message);
      })
      .then(() => {
        this.setMarks(data);
        return this.map;
      });
  }

  private setMarks(data: { lat: number; lon: number }[]): void {
    const objectManager = new this.maps.ObjectManager();

    data.forEach((item: any, index: number) => {
      objectManager.add(this.getObject({ id: index, lat: item.lat, lon: item.lon }));
    });

    this.map.geoObjects.add(objectManager);
  }

  private getObject({ id, lat, lon }: { id: number; lat: number; lon: number }) {
    return {
      type: 'Feature',
      id: id,
      geometry: {
        type: 'Point',
        coordinates: [lat, lon]
      },
      properties: {
        hintContent: 'LoverFlower'
      }
    };
  }
}

