import {
  Directive,
  DoCheck,
  Input,
  KeyValueChanges,
  KeyValueDiffer,
  KeyValueDiffers,
  TemplateRef,
  ViewContainerRef
} from '@angular/core';

@Directive({
  selector: '[appForEntries]'
})
export class ForEntriesDirective implements DoCheck {
  @Input() appForEntriesOf: { [key: string]: unknown } = {};

  objectDiffer: KeyValueDiffer<string, unknown>;

  constructor(
    private templateRef: TemplateRef<unknown>,
    private viewContainerRef: ViewContainerRef,
    private keyValueDiffers: KeyValueDiffers
  ) {
    this.objectDiffer = this.keyValueDiffers.find(this.appForEntriesOf).create();
  }

  ngDoCheck(): void {
    const objectChanges: KeyValueChanges<string, unknown> | null = this.objectDiffer.diff(
      this.appForEntriesOf
    );
    if (objectChanges) {
      this.viewContainerRef.clear();
      objectChanges.forEachAddedItem((record) => {
        this.viewContainerRef.createEmbeddedView(this.templateRef, {
          $implicit: {
            name: record.key,
            value: record.currentValue
          }
        });
      });
    }
  }
}

import { Directive, DoCheck, ElementRef, HostListener, Input } from '@angular/core';

@Directive({
  selector: 'img[appImageUploadStatus]',
  exportAs: 'appImageUploadStatus'
})
export class ImageUploadStatusDirective implements DoCheck {
  private elementWithSettedClass: HTMLElement | null;
  @Input() classNameLoading = 'image-loading';
  @Input() otherTargetForOutput: HTMLElement;

  constructor(private elRef: ElementRef) {}

  ngDoCheck(): void {
    if (!this.elRef.nativeElement.complete && !this.elementWithSettedClass) {
      const targetForStatusOutput = this.otherTargetForOutput || this.elRef.nativeElement;
      targetForStatusOutput.classList.add(this.classNameLoading);
      this.elementWithSettedClass = targetForStatusOutput;
    }
  }

  @HostListener('load')
  private handlerOnLoad(): void {
    if (!this.elementWithSettedClass) {
      return;
    }
    this.elementWithSettedClass.classList.remove(this.classNameLoading);
    this.elementWithSettedClass = null;
  }
}

import { Directive, ElementRef, EventEmitter, Input, Output } from '@angular/core';

@Directive({
  selector: '[appIntersection]'
})
export class IntersectionDirective {
  private observer: IntersectionObserver;
  @Input() rootMargin = '100';
  @Output() appIntersection = new EventEmitter<boolean>();

  constructor(public elRef: ElementRef) {
    this.observer = new IntersectionObserver(this.callback, {
      rootMargin: `${this.rootMargin}px`,
      threshold: 0.5,
      root: null
    });
    this.observer.observe(this.elRef.nativeElement);
  }

  private callback: ConstructorParameters<typeof IntersectionObserver>[0] = (entries) =>
    entries
      .filter((entry) => entry.isIntersecting)
      .forEach((entry) => {
        const rootBounds = entry.rootBounds;
        if (!rootBounds) {
          return;
        }
        const isAbove = entry.boundingClientRect.y < entry.rootBounds.y;
        this.appIntersection.emit(isAbove);
      });
}

