import { AfterViewInit, Directive, ElementRef, Inject, Input, OnDestroy } from '@angular/core';
import { Observable, Subject, takeUntil, throttleTime } from 'rxjs';
import {
  IPointerEvent,
  PointerEventService
} from 'src/app/services/events/pointer/pointer-event.service';
import { TargetVariant } from './types';
import { WINDOW_TOKEN } from 'src/app/tokens/window';

interface EventHandlerArgs {
  element: HTMLElement;
  bgOffsetX: number;
  bgOffsetY: number;
  pointerEvent: IPointerEvent;
}

@Directive({
  selector: '[appParallax]'
})
export class ParallaxDirective implements AfterViewInit, OnDestroy {
  private destroySubject: Subject<null>;
  private durationAnim = 0.5;
  private pointerEvent$: Observable<IPointerEvent>;
  @Input() appParallaxBgOffsetX = 0;
  @Input() appParallaxBgOffsetY = 0;
  @Input() appParallaxTargetVariant: TargetVariant = 'background';
  @Input() appParallaxScale = 1;
  @Input() appParallaxThrottleTime = 100;

  constructor(
    @Inject(WINDOW_TOKEN) private window: Window,
    pointerEventService: PointerEventService,
    private elRef: ElementRef<HTMLElement>
  ) {
    this.destroySubject = new Subject();
    this.pointerEvent$ = pointerEventService.pointermoveObservable$;
  }

  get host(): HTMLElement {
    return this.elRef.nativeElement;
  }

  ngAfterViewInit(): void {
    this.setParallax(
      this.host,
      this.appParallaxBgOffsetX,
      this.appParallaxBgOffsetY,
      this.durationAnim
    );
  }

  ngOnDestroy(): void {
    this.pointerEvent$;
  }

  private setParallax(
    element: HTMLElement,
    bgOffsetX: number,
    bgOffsetY: number,
    durationAnim: number = this.durationAnim
  ): void {
    this.setPreparatoryStyles(element, durationAnim);

    this.pointerEvent$
      .pipe(takeUntil(this.destroySubject), throttleTime(this.appParallaxThrottleTime))
      .subscribe((pointerEvent) => {
        this.backgroundMove({ element, bgOffsetX, bgOffsetY, pointerEvent });
      });
  }

  private setPreparatoryStyles(element: HTMLElement, durationAnim: number): void {
    if (this.appParallaxTargetVariant === 'background') {
      element.style.setProperty('transition', `background-position ${durationAnim}s ease-out`);
    }
    if (this.appParallaxTargetVariant === 'img') {
      element.style.setProperty('transition', `transform ${durationAnim}s ease-out`);
    }
  }

  private backgroundMove({ element, bgOffsetX, bgOffsetY, pointerEvent }: EventHandlerArgs) {
    if (pointerEvent.pointerType !== 'mouse') {
      return;
    }
    const offsetX = (pointerEvent.clientX / this.window.innerWidth) * bgOffsetX - bgOffsetX;
    const offsetY = (pointerEvent.clientY / this.window.innerHeight) * bgOffsetY - bgOffsetY;

    if (this.appParallaxTargetVariant === 'background') {
      element.style.setProperty('background-position', `${offsetX}px ${offsetY}px`);
    }
    if (this.appParallaxTargetVariant === 'img') {
      element.style.setProperty(
        'transform',
        `translate(${offsetX}px, ${offsetY}px) scale(${this.appParallaxScale})`
      );
    }
  }
}

import { Directive, ElementRef, Input, OnInit, Renderer2 } from '@angular/core';

const buttonVariantClasses = <const>{
  '1': 'button-1',
  '2': 'button-2',
  '3': 'button-3',
  '4': 'button-4'
};

export type ButtonVariant = keyof typeof buttonVariantClasses;

@Directive({
  selector: 'button[appUiButton], a[appUiButton]'
})
export class UiButtonDirective implements OnInit {
  private buttonVariantClasses = buttonVariantClasses;
  private _variant: ButtonVariant;
  private defaultVariant = <const>'1';

  @Input('appUiButton')
  set variant(value: ButtonVariant | '') {
    this._variant =
      value !== '' && value in this.buttonVariantClasses ? value : this.defaultVariant;
  }

  constructor(private elRef: ElementRef, private renderer: Renderer2) {}

  ngOnInit(): void {
    this.initButton();
  }

  private get host() {
    return this.elRef.nativeElement;
  }

  private initButton() {
    this.renderer.addClass(this.host, this.buttonVariantClasses[this._variant]);
  }
}

import {
  AfterViewInit,
  Directive,
  ElementRef,
  EventEmitter,
  Inject,
  Input,
  OnDestroy,
  OnInit,
  Output
} from '@angular/core';
import { Scroll } from '@angular/router';
import { Observable, Subject, asyncScheduler, takeUntil, throttleTime } from 'rxjs';
import { ScrollEventService } from 'src/app/services/events/scroll/scroll-event.service';
import { WINDOW_TOKEN } from 'src/app/tokens/window';

type Action = 'hidden' | 'visible';

@Directive({
  selector: '[appVisibilityByScroll]',
  exportAs: 'appVisibilityByScroll'
})
export class VisibilityByScrollDirective implements OnInit, AfterViewInit, OnDestroy {
  private destroySubject: Subject<null> = new Subject();
  private prevScroll: number;
  private currentScroll: number;
  private scrollObservable$: Observable<Scroll>;
  private isVisibleHost = true;
  @Input() inactionTop = 0;
  @Output() emitIsOnMove: EventEmitter<boolean>;

  get host(): HTMLElement {
    return this.elRef.nativeElement;
  }

  constructor(
    @Inject(WINDOW_TOKEN) private window: Window,
    private elRef: ElementRef<HTMLElement>,
    private scrollService: ScrollEventService
  ) {
    this.emitIsOnMove = new EventEmitter();
    this.scrollObservable$ = this.scrollService.scrollObservable$;
    this.currentScroll = this.getPageYOffset();
  }

  ngOnInit(): void {
    this.subscribeToScroll();
  }

  ngAfterViewInit(): void {
    this.setPreporatoryStyles();
  }

  ngOnDestroy(): void {
    this.destroySubject.next(null);
    this.destroySubject.complete();
  }

  private subscribeToScroll() {
    this.scrollObservable$
      .pipe(takeUntil(this.destroySubject), throttleTime(400, asyncScheduler, { trailing: true }))
      .subscribe(() => {
        this.switchVisible();
      });
  }

  private setPreporatoryStyles() {
    this.host.style.setProperty('position', 'sticky');
    this.host.style.setProperty('transition', 'transform 0.2s');
    this.host.style.setProperty('transform', 'translateY(0)');
  }

  private switchVisible(): void {
    this.currentScroll = this.getPageYOffset();

    if (this.currentScroll <= this.inactionTop) {
      this.changeVisibility('visible');
      this.emitIsOnMove.emit(false);
      return;
    }
    if (this.currentScroll > this.prevScroll && this.isVisibleHost) {
      this.changeVisibility('hidden');
      this.emitIsOnMove.emit(true);
    }
    if (this.currentScroll < this.prevScroll && this.isVisibleHost) {
      this.changeVisibility('visible');
      this.emitIsOnMove.emit(true);
    }

    this.prevScroll = this.currentScroll;
  }

  private changeVisibility(action: Action): void {
    if (action === 'hidden') {
      this.host.style.setProperty('transform', 'translateY(-100%)');
      return;
    }
    this.host.style.setProperty('transform', 'translateY(0)');
  }

  private getPageYOffset(): number {
    return this.window.scrollY;
  }
}

