  }
}

@keyframes animOpacity {
  from {
    transform: opacity(1);
  }

  to {
    transform: opacity(0);
  }
}

.card-item {
  &:first-child {
    position: relative;
    grid-row: 5/11;
    grid-column: 1/2;

    &:after {
      content: '';
      position: absolute;
      top: 9%;
      left: -23%;
      z-index: -2;
      font-size: 200px;
      color: $color-10;
      animation: animShift 20s linear infinite alternate-reverse;
      transition: transform 3s;
      transform: translateX(0);
