  &:nth-child(2) {
    position: relative;
    grid-column: 3/4;
    grid-row-start: span 6;

    &:after {
      content: '';
      position: absolute;
      top: 12%;
      right: 36px;
      z-index: -2;
      font-size: 200px;
      color: $color-10;
      animation: animShift 17s linear infinite alternate;
      transform: translateX(0);
    }
  }

  &:last-child {
    position: relative;
    grid-column: 2/4;
