import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  OnDestroy,
  OnInit,
  ViewChild
} from '@angular/core';
import { CorporateClientsComponent } from './component/corporate-clients.component';
import { breadcrumbs } from './data/pageData';
import { ICorporateUser } from './component/types';
import { CorporateClientsService } from 'src/app/services/corporate/corporate-clients.service';
import { Subject, takeUntil } from 'rxjs';
import { Meta } from '@angular/platform-browser';
import { description } from './data/meta';

@Component({
  selector: 'app-corporate-clients-container',
  templateUrl: './corporate-clients-container.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class CorporateClientsContainerComponent implements OnInit, OnDestroy {
  private destroySubject: Subject<null> = new Subject();
  @ViewChild('corporateClientsComponent')
  corporateClientsComponent: CorporateClientsComponent;
  public breadcrumbs = breadcrumbs;
  public isLoading = false;

  constructor(
    private cdr: ChangeDetectorRef,
    private corporateClientsService: CorporateClientsService,
    private meta: Meta
  ) {
    this.setMeta();
  }

  ngOnInit(): void {
    this.subscribeToCorporateLoading();
    this.subscribeToResetForm();
  }

  ngOnDestroy(): void {
    this.destroySubject.next(null);
    this.destroySubject.complete();
  }

  private setMeta(): void {
    this.meta.addTag({
      name: 'description',
      content: description
    });
  }

  private subscribeToCorporateLoading() {
    this.corporateClientsService
      .getRequestLoading()
      .pipe(takeUntil(this.destroySubject))
      .subscribe((isLoading) => {
        this.isLoading = isLoading;
        this.cdr.markForCheck();
      });
  }

  private subscribeToResetForm(): void {
    this.corporateClientsService.clearFormStream
      .pipe(takeUntil(this.destroySubject))
      .subscribe(() => {
        this.corporateClientsComponent.resetForm();
        this.cdr.markForCheck();
      });
  }

  public handlerSubmitForm(data: ICorporateUser) {
    this.corporateClientsService.submitForm(data);
  }
}

import {
  ChangeDetectionStrategy,
  Component,
  EventEmitter,
  Input,
  OnDestroy,
  OnInit,
  Output
} from '@angular/core';
import { ControlValueAccessor, FormControl } from '@angular/forms';
import { Subject, takeUntil } from 'rxjs';
import { UtilsService } from 'src/app/services/utils/utils.service';
import { OnChangeCallback, OnTouchedCallback } from '~types/controlValueAccessor';

@Component({
  selector: 'app-counter',
  templateUrl: './counter.component.html',
  styleUrls: ['./counter.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class CounterComponent implements OnInit, OnDestroy, ControlValueAccessor {
  protected id = this.utilsService.generateRandomString();
  private destroySubject: Subject<null>;
  protected countControl = new FormControl(0, { nonNullable: true });
  private onChangeCallBack: OnChangeCallback<number>;
  protected onTouchedCallBack: OnTouchedCallback;
  @Input() min: number;
  @Input() max: number;
  @Input() step = 1;
  @Input() set value(value: number) {
    this.countControl.setValue(value);
  }
  @Input() labelValueA11y: string;
  @Output() valueChange: EventEmitter<number> = new EventEmitter();
  @Output() emitDecrValue: EventEmitter<number> = new EventEmitter();
  @Output() emitIncrValue: EventEmitter<number> = new EventEmitter();

  constructor(private utilsService: UtilsService) {}

  ngOnInit(): void {
    this.destroySubject = new Subject();
    this.subscribeToValueChanges();
  }

  ngOnDestroy(): void {
    this.destroySubject.next(null);
    this.destroySubject.complete();
  }

  private subscribeToValueChanges(): void {
    this.countControl.valueChanges.pipe(takeUntil(this.destroySubject)).subscribe((value) => {
      if (value < this.min) {
        return this.countControl.setValue(this.min);
      }
      if (value > this.max) {
        return this.countControl.setValue(this.max);
      }
      this.onChange(value);
    });
  }

  private onChange(value: number): void {
    this.valueChange.emit(value);

    if (this.onChangeCallBack) {
      this.onChangeCallBack(value);
    }
  }

  protected onTouched(): void {
    if (!this.onTouchedCallBack) {
      return;
    }
    this.onTouchedCallBack();
  }

  get isDisabledDecrButton(): boolean {
    return this.min >= this.countControl.value;
  }

  get isDisabledIncButton(): boolean {
    return this.max <= this.countControl.value;
  }

  protected doDecrement(): void {
    this.countControl.setValue(this.countControl.value - 1);
  }
  protected doIncrement(): void {
    this.countControl.setValue(this.countControl.value + 1);
  }

  public writeValue(value: number): void {
    this.value = value;
  }

  public registerOnChange(fn: OnChangeCallback<number>): void {
    this.onChangeCallBack = fn;
  }
  public registerOnTouched(fn: OnTouchedCallback): void {
    this.onTouchedCallBack = fn;
  }
}

