.dropdown {
  position: relative;
  display: inline-block;
  width: 100%;
}

.dropdown__content {
  position: absolute;
  top: 100%;
  z-index: 2;
  min-width: 100%;
  padding-top: 8px;
  transition: opacity 0.2s;

  &--position {
    &-center {
      left: 50%;
      transform: translateX(-50%);
    }

    &-left {
      right: 0;
    }

    &-right {
      left: 0;
    }
  }

  &--visibility {
    &-hidden {
      opacity: 0;
      visibility: hidden;
    }

    &-visible {
      opacity: 1;
      visibility: visible;
    }
  }
}

