@import 'colors';
@import 'media';

.header {
  width: 100%;
  transition: background-color 0.3s;

  &--bg-black {
    background-color: $color-5;
  }

  &--bg-transparent {
    background-color: transparent;
  }

  &__container {
    display: flex;
    align-items: center;
    padding-top: 9px;
    padding-bottom: 9px;
  }

  &__btn-burger {
    display: none;
  }

  &__logo-link {
    margin-right: 60px;
  }

  &__phone {
    display: inline-block;
    margin-right: 20px;
    margin-left: auto;
    white-space: nowrap;
  }
}

.container {
  &__form-search {
    margin-right: 20px;
  }
}

.btn-burger {
  position: relative;
  width: 40px;
  height: 24px;

  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 2px;
    background-color: $color-1;
    transform: translateY(-50%);
  }

  &__line-pivot {
    width: 100%;
    height: 100%;

    &::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 2px;
      background-color: $color-1;
    }

    &::before {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 2px;
      background-color: $color-1;
    }
  }
}

.logo-link {
  display: inline-block;

  &__text {
    display: none;
    max-width: 140px;
    font-size: 20px;
    font-family: 'Cormorant', sans-serif;
    font-weight: 300;
    text-align: center;
    letter-spacing: 5px;
  }
}

.nav {
  display: flex;
  flex-basis: 495px;
  flex-shrink: 1;
  flex-grow: 0;

  &--mr {
    margin-right: 40px;
  }

  &__nav-list {
    width: 100%;
  }
}

.nav-list {
  &--flex {
    display: flex;
  }

  &--justify-between {
    justify-content: space-between;
  }

  &__item {
    &--mr:not(:last-child) {
      margin-right: 15px;
    }
  }
}

.menu-wrapper {
  background-color: rgb($color: $color-9, $alpha: 0.9);

  &__list {
    width: max-content;
    padding: 10px 20px;
  }

  &__item {
    text-transform: uppercase;

    &:not(:last-child) {
      margin-bottom: 7px;
    }
  }

  &__link {
    display: block;
  }
}

.menu-link {
  transition: color 0.3s;
  &:hover {
    color: rgb($color: $color-1, $alpha: 0.9);
  }
  &:active {
    color: rgb($color: $color-1, $alpha: 0.7);
  }
}

.nav-link {
  position: relative;
  transition: color 0.3s;

  &::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: $color-1;
    opacity: 0;
    transition: opacity 0.3s;
  }

  &:hover {
    &::after {
      opacity: 1;
    }
  }

  &:active {
    color: rgb($color: $color-1, $alpha: 0.8);
  }
}

@media (max-width: $ui-md) {
  .nav {
    display: none;
  }

  .container__form-search {
    display: none;
  }

  .header {
    &__container {
      justify-content: space-between;
    }

    &__btn-burger {
      display: inline-block;
    }

    &__logo-link {
      margin-right: 0;
    }

    &__btn-cart {
      margin-left: 0;
    }

    &__phone {
      display: none;
    }
  }
}

@media (max-width: $ui-xs) {
  .header {
    padding-top: 20px;
    padding-bottom: 20px;
  }

  .logo-link {
    &--main {
      display: none;
    }

    &__icon {
      display: none;
    }

    &__text {
      display: inline-block;
    }
  }

  .btn-burger {
    &__line-pivot {
      &::after {
        top: 2px;
      }

      &::before {
        bottom: 2px;
      }
    }
  }
}

