    grid-column-gap: 15px;
    grid-row-gap: 20px;
  }
  .how-to-order {
    background-size: 155px;
  }

  .list-wrapper {
    padding-left: 140px;
  }
}

@media (max-width: $ui-xs) {
  .steps {
    grid-template-columns: 0.33fr 0.33fr 0.33fr 1fr;
    grid-column-gap: 0;
    grid-row-gap: 20px;
    padding-right: 0;

    &__title {
      font-size: 20px;
    }

    &::before {
      content: none;
    }

    &::after {
      left: 50%;
    }
  }

  .step {
    &:first-child {
      grid-column: 4/-1;
      grid-row: 1/2;
    }
    &:nth-child(2) {
      grid-column: 3/-1;
      grid-row: 2/3;
    }

    &:nth-child(3) {
      grid-column: 2/-1;
      grid-row: 3/4;
    }

    &:nth-child(4) {
      grid-column: 1/-1;
      grid-row: 4/5;
    }
    &:nth-child(5) {
      grid-column: 1/-1;
      grid-row: 5/6;
    }
  }

