  }

  &__loader {
    max-width: 200px;
    margin: 0 auto;
  }

  &__error {
    max-width: 200px;
    margin: 0 auto;
  }

  &__product-section {
    margin-bottom: 80px;
  }

  &__additionally {
    margin-bottom: 100px;
  }

  &__tabs {
    margin-bottom: 100px;
  }
}

.about-product {
  display: flex;

  &__link {
    display: none;
    padding-left: 10px;
  }

  &__product-images-wrapper {
    display: flex;
    width: 50%;
    margin-right: 30px;
  }

  &__descr-product {
    width: 50%;
  }
}

.product-images-wrapper {
  &__images-slider {
    flex: 1 1 30%;
    margin-right: 20px;
    height: 100%;
  }

  &__wrapper-image {
    flex: 1 1 70%;
  }

  &__slide-content-wrapper {
    height: 100%;
    padding: 3px;
  }

  &__slide-btn {
    height: 100%;
    padding: 0;
  }

  &__slide-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
}

.images-slider {
  display: flex;
  flex-direction: column;

  &__slider {
    width: 100%;
    height: 100%;
  }
}

.control-btn {
  width: 100%;
  padding: 10px;
  background-color: transparent;
  transition: background-color 0.3s;

  &::after {
    top: 50%;
    left: 50%;
    height: 100%;
  }

  &--prev {
    &::after {
      transform: translate(-50%, -50%) rotateZ(90deg);
    }
  }

  &--next {
    &::after {
      transform: translate(-50%, -50%) rotateZ(-90deg);
    }
  }

  &:hover {
    background-color: rgb($color: $color-1, $alpha: 0.3);
  }

  &:active {
    background-color: rgb($color: $color-1, $alpha: 0.5);
  }
}

.slide-btn {
  position: relative;
  width: 100%;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    background-color: $color-1;
    opacity: 0;
    transition: opacity 0.3s;
  }

  &--active {
    &::after {
      opacity: 0.4;
    }
  }
}

.wrapper-image {
  display: flex;
  justify-content: center;
  align-items: flex-start;

  &__image {
    object-fit: contain;
    height: 100%;
    max-height: 100%;
  }
}

.descr-product {
  &__link {
    display: inline-block;
    margin-bottom: 20px;
  }

  &__title {
    margin-bottom: 15px;
    font-size: 40px;
    font-family: 'Cormorant', sans-serif;
  }

  &__subtitle {
    margin-right: 1em;
  }

  &__item {
    margin-left: 0.5em;

    &:not(:last-of-type) {
      &:after {
        content: ',';
      }
    }
  }

  &__price {
    margin-bottom: 20px;
  }

  &__descr-paragraph-1 {
    margin-bottom: 20px;
  }

  &__descr-paragraph-2 {
    margin-bottom: 20px;
  }

  &__descr-paragraph-3 {
    margin-bottom: 70px;
  }
}

.wrapper-controls {
  display: flex;
  align-items: center;

  &__btn {
    margin-right: 30px;
    flex: 0 1 50%;
  }

  &__counter {
    flex: 0 1 30%;
  }
}

.additionally {
  &__title {
    margin-bottom: 45px;
    font-size: 20px;
    text-align: center;
  }
}

.additionally-list {
  display: flex;

  &__additionally-item {
    display: flex;
    flex-direction: column;
    flex-basis: 100%;
    justify-content: flex-start;
    align-items: center;
    padding: 30px 16px;
    border-radius: 20px;
    font-size: 20px;
    text-align: center;
    line-height: 1.3;

    &:not(:last-child) {
      margin-right: 30px;
    }
  }
}

.additionally-item {
  position: relative;

  &::after {
    content: '';
    position: absolute;
    top: -15px;
    left: 50%;
    width: 30px;
    height: 30px;
    background-color: #000000;
    border-radius: 50%;
    transform: translateX(-50%);
  }

  &__title {
    min-height: 3em;
    margin-bottom: 15px;
    font-size: 20px;
    font-weight: 300;
  }

  &__paragraph {
    align-self: center;
    font-size: 14px;
  }
}

.tabs {
  &--bg {
    position: relative;

    &::after {
      content: '';
      position: absolute;
      right: 0;
      top: 100px;
      z-index: -1;
      width: 335px;
      height: 501px;
      background-image: image-set(
        url('/assets/images/pages/product/foliage-bg.webp') type('image/webp'),
        url('/assets/images/pages/product/foliage-bg.png')
      );
      background-size: contain;
      background-position: center right;
      background-repeat: no-repeat;
    }
  }
}

.tabs-list {
  position: relative;
  display: flex;
  justify-content: center;

  &::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: #555555;
  }
}

.tabs-item {
  flex: 1 1 100%;

  &__btn {
    width: 100%;
    padding: 30px 40px;
    position: relative;
    color: #555555;

    &::before {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 3px;
      border-radius: 3px;
      background-color: #43ffd2;
      opacity: 0;
      transform: scaleX(0);
      transition: transform 0.3s;
    }

    &[data-active='true'] {
      color: #43ffd2;

      &::before {
        opacity: 1;
        transform: scaleX(1);
      }
    }
  }
}

.offer {
  &__title {
    margin-bottom: 25px;
    font-size: 30px;
    font-weight: 700;
  }
}

@media (max-width: $ui-md) {
  .about-product {
    &__product-images-wrapper {
      flex-direction: column-reverse;
    }
  }

  .product-images-wrapper {
    &__wrapper-image {
      margin-bottom: 20px;
    }

    &__images-slider {
      margin-right: 0;
    }
  }

  .images-slider {
    flex-direction: row;

    &__control-btn {
      width: initial;
    }
  }

  .control-btn {
    &--prev {
      &::after {
        transform: translate(-50%, -50%) rotateZ(0deg);
      }
    }
    &--next {
      &::after {
        transform: translate(-50%, -50%) rotateZ(180deg);
      }
    }
  }

  .additionally-list {
    flex-direction: column;
    align-items: center;

    &__additionally-item {
      &:not(:last-child) {
        margin-right: 0;
        margin-bottom: 20px;
      }
    }
  }

  .additionally-item {
    max-width: 65%;

    &__title {
      min-height: initial;
    }
  }
}

@media (max-width: $ui-sm) {
  .wrapper-controls {
    &__btn {
      margin-right: 15px;
    }

    &__counter {
      flex-grow: 1;
    }
  }
}

@media (max-width: $ui-xs) {
  .product {
    &__container {
      padding-bottom: 100px;
    }

    &__product-section {
      margin-bottom: 40px;
    }

    &__tabs {
      margin-bottom: 0;
    }
  }

  .descr-product {
    &__link {
      display: none;
    }

    &__title {
      font-size: 20px;
      margin-bottom: 10px;
    }

    &__descr-paragraph-1 {
      font-size: 14px;
    }

    &__descr-paragraph-2 {
      font-size: 14px;
    }

    &__descr-paragraph-3 {
      font-size: 12px;
    }

    &__descr-paragraph-4 {
      font-size: 12px;
      margin-bottom: 30px;
    }
  }

  .about-product {
    flex-direction: column;

    &__product-images-wrapper {
      width: 100%;
      margin-right: 0;
      margin-bottom: 30px;
    }

    &__descr-product {
      width: 100%;
    }

    &__link {
      display: inline-block;
    }
  }

  .wrapper-controls {
    &__btn {
      margin-right: 10px;
      flex-basis: 60%;
    }
  }

  .additionally {
    &__title {
      margin-bottom: 35px;
    }
  }

  .additionally-item {
    max-width: 85%;

    &::after {
      width: 20px;
      height: 20px;
      top: -10px;
    }

    &__title {
      font-size: 14px;
    }
  }

  .tabs {
    &__tabs-wrapper {
      margin-bottom: 60px;
    }
  }

  .offer {
    &__title {
      font-size: 20px;
    }
  }
}

