:host {
  display: block;
}

.radio-wrapper {
  display: flex;
  align-items: center;

  &__custom-radio {
    position: relative;
    display: inline-block;
    width: 16px;
    height: 16px;
    margin-right: 12px;
    pointer-events: none;

    &::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      border: 1px solid $color-4;
      border-radius: 50%;
      outline-color: transparent;
      outline-width: 2px;
      outline-style: solid;
      transition: outline-color 0.3s;
    }

    &::after {
      content: "";
      position: absolute;
      top: 50%;
      left: 50%;
      z-index: 3;
      width: 9px;
      height: 9px;
      background-color: $color-4;
      border-radius: 50%;
      opacity: 0;
      transform: translate(-50%, -50%) scale(0);
      transition: transform 0.3s;
    }
  }

  &__input {
    position: absolute;
    width: 20px;
    height: 20px;
    opacity: 0;
    cursor: pointer;

    &:checked ~ .radio-wrapper__custom-radio {
      &::after {
        opacity: 1;
        transform: translate(-50%, -50%) scale(1);
      }
    }

    &:focus ~ .radio-wrapper__custom-radio {
      &::before {
        outline-color: $color-1;
      }
    }
  }

  &__label {
    cursor: pointer;
    font-size: 14px;
  }
}

