@import 'media';

.tabs-list-btns {
  position: relative;
  display: flex;
  justify-content: center;

  &::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: #555555;
  }
}

.tabs-item-btn {
  flex: 1 1 100%;

  &__btn {
    display: block;
    width: 100%;
    padding: 30px 40px;
    position: relative;
    color: #555555;
    text-align: center;

    &::before {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 3px;
      border-radius: 3px;
      background-color: #43ffd2;
      opacity: 0;
      transform: scaleX(0);
      transition: transform 0.3s;
    }

    &[data-active='true'] {
      color: #43ffd2;

      &::before {
        opacity: 1;
        transform: scaleX(1);
      }
    }

    &:focus {
      outline: 1px solid #43ffd2;
      outline-offset: 5px;
    }
  }
}

.tabs-wrapper {
  margin: 0 auto 60px;
  max-width: 720px;
}

@media (max-width: $ui-xs) {
  .tabs-item-btn {
    &__btn {
      padding: 15px 10px;
    }
  }
}

