import localeRu from '@angular/common/locales/ru';

import { AppComponent } from './app.component';
import { AppRoutingModule } from './app.routing.module';
import { StoreModule } from '@ngrx/store';
import { HttpClientModule } from '@angular/common/http';
import { StoreDevtoolsModule } from '@ngrx/store-devtools';
import { environment } from 'src/environments/environment';
import { AppContentModule } from './page-components/app-content.module';
import { EffectsModule } from '@ngrx/effects';
import { ProductsEffects } from './store/products/products.effects';
import { AppEffects } from './store/app/app.effects';
import { CartEffects } from './store/cart/cart.effects';
import { FeedbackEffects } from './store/feedback/feedback.effects';
import { OrderEffects } from './store/order/order.effects';
import { CorporateEffects } from './store/corporate/corporate.effects';
import { NotificationsEffects } from './store/notifications/notifications.effects';
import { WINDOW_TOKEN } from './tokens/window';
import { LOCAL_STORAGE_TOKEN } from './tokens/localStorage';
import { rootReducer } from './store/root.reducer';

registerLocaleData(localeRu);

@NgModule({
  declarations: [AppComponent],
  imports: [
    BrowserModule.withServerTransition({ appId: 'serverApp' }),
    AppRoutingModule,
    BrowserAnimationsModule,
    HttpClientModule,
    AppContentModule,
    StoreModule.forRoot(rootReducer),
    StoreDevtoolsModule.instrument({ maxAge: 25, logOnly: !isDevMode() }),
    StoreDevtoolsModule.instrument({
      name: 'NGRX LOVER FLOWER STORE',
      maxAge: 25,
      logOnly: !environment.production,
      autoPause: true,
      trace: true
    }),
    EffectsModule.forRoot([
      ProductsEffects,
      CartEffects,
      FeedbackEffects,
      OrderEffects,
      CorporateEffects,
      NotificationsEffects,
      AppEffects
    ])
  ],
  providers: [
    {
      provide: LOCALE_ID,
      useValue: 'ru'
    },
    {
      provide: LOCAL_STORAGE_TOKEN,
      useFactory: () => localStorage
    },
    {
      provide: WINDOW_TOKEN,
      useFactory: () => window
    }
  ],
  bootstrap: [AppComponent]
})
export class AppModule {}

import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { ExtraOptions, RouterModule, Routes } from '@angular/router';
import { productResolver } from './guards/product.guard';
import { pages } from './CEO';

const routerConfig: ExtraOptions = {
  anchorScrolling: 'enabled',
  initialNavigation: 'enabledBlocking'
};

const routes: Routes = [
  {
    path: '',
    title: `  ${TITLE}`,
    loadChildren: () =>
      "import('./pages/main/main-container.module').then((m) => m.MainContainerModule)"
  },
  {
    path: 'catalog',
    title: ` ${TITLE}`,
    loadChildren: () =>
      "import('./pages/catalog/catalog-container.module').then((m) => m.CatalogContainerModule)"
  },
  {
    path: 'product/:id',
    title: `  ${TITLE}`,
    loadChildren: () =>
      "import('./pages/product/product-container.module').then((m) => m.ProductContainerModule)",
    resolve: { product: productResolver }
  },
  {
    path: 'order',
    loadChildren: () =>
      "import('./pages/order/order-container.module').then((m) => m.OrderContainerModule)"
  },
  {
    path: 'searching-results',
    title: `  ${TITLE}`,
    loadChildren: () =>
      "import('./pages/searching-results/searching-results-container.module').then(\
        (m) => m.SearchingResultsContainerModule\
      )"
  },
  {
    path: 'faq',
    title: `FAQ ${TITLE}`,
    loadChildren: () => "import('./pages/faq/faq.module').then((m) => m.FaqModule)"
  },
  {
    path: 'delivery-payment',
    title: `   ${TITLE}`,
    loadChildren: () =>
      "import('./pages/delivery-payment/delivery-payment.module').then(\
        (m) => m.DeliveryPaymentModule\
      )"
  },
  {
    path: 'about-us',
    title: `  ${TITLE}`,
    loadChildren: () => "import('./pages/about-us/about-us.module').then((m) => m.AboutUsModule)"
  },
  {
    path: 'corporate-clients',
    title: `  ${TITLE}`,
    loadChildren: () =>
      "import('./pages/corporate-clients/corporate-clients-container.module').then(\
        (m) => m.CorporateClientsContainerModule\
      )"
  },
  {
    path: 'contacts',
    title: ` ${TITLE}`,
    loadChildren: () => "import('./pages/contacts/contacts.module').then((m) => m.ContactsModule)"
  },
  {
    path: '**',
    title: `   ${TITLE}`,
    loadChildren: () => "import('./pages/not-found/not-found.module').then((m) => m.NotFoundModule)"
  }
];

@NgModule({
  imports: [CommonModule, RouterModule.forRoot(routes, routerConfig)],
  exports: [RouterModule]
})
export class AppRoutingModule {}

import { NgModule, inject } from '@angular/core';
import { ServerModule } from '@angular/platform-server';

import { AppModule } from './app.module';
import { AppComponent } from './app.component';
import { LOCAL_STORAGE_TOKEN } from './tokens/localStorage';
import { WINDOW_TOKEN } from './tokens/window';
