createSelector<IRootState, IState, IState['inCart'], number>(
  cartSelector,
  inCartSelector,
  (_, inCart: IState['inCart']) => {
    return inCart
      ? inCart.reduce(
          (accum, productInCart) =>
            productInCart.count *
              (productInCart.product.price.value - productInCart.product.price.discount) +
            accum,
          0
        )
      : 0;
  }
)

createReducer<IState>(
  initialState,
  on(requestAction, (state) => ({
    ...state,
    isLoading: true,
    error: null
  })),
  on(requestSuccessAction, (state, payload) => ({
    ...state,
    inCart: payload.inCart,
    isLoading: false
  })),
  on(requestErrorAction, (state, payload) => ({
    ...state,
    isLoading: false,
    error: payload.error
  })),
  on(postProductAction, (state) => ({
    ...state,
    isLoading: true,
    error: null
  })),
  on(postProductSuccessAction, (state, payload) => ({
    ...state,
    inCart: payload.inCart,
    isLoading: false
  })),
  on(postProductErrorAction, (state, payload) => ({
    ...state,
    isLoading: false,
    postError: payload.error
  })),
  on(postChangeCountAction, (state) => ({
    ...state,
    isLoading: true,
    postError: null
  })),
  on(postChangeCountSuccessAction, (state, payload) => ({
    ...state,
    inCart: payload.inCart,
    isLoading: false
  })),
  on(postChangeCountErrorAction, (state, payload) => ({
    ...state,
    isLoading: false,
    error: payload.error
  })),
  on(deleteProductAction, (state) => ({
    ...state,
    isLoading: true,
    error: null
  })),
  on(deleteProductSuccessAction, (state, payload) => ({
    ...state,
    inCart: payload.inCart,
    isLoading: false
  })),
  on(deleteProductErrorAction, (state, payload) => ({
    ...state,
    isLoading: false,
    error: payload.error
  }))
)

