createReducer<IState>(
  initialState,
  on(hydrateSuccessAction, (store, action) => ({
    ...store,
    ...action.payload,
    isHydrateSuccess: true
  })),
  on(hydrateErrorAction, (store) => ({
    ...store
  })),
  on(requestAction, (store) => ({
    ...store,
    isLoading: true,
    error: null
  })),
  on(requestSuccessAction, (state, payload) => ({
    ...state,
    isLoading: false,
    product: payload.product
  })),
  on(requestErrorAction, (state, payload) => ({
    ...state,
    isLoading: false,
    error: payload.error
  })),
  on(requestCancelAction, (state) => ({
    ...state,
    isLoading: false,
    isHydrateSuccess: false
  }))
)

(route: ActivatedRouteSnapshot) => {
  const productContainerService = inject(ProductContainerService);
  const productId = Number(route.paramMap.get('id'));

  productContainerService.requestProduct(productId);

  return of(productId);
}

