createSelector<IRootState, IState, IState['inCart'], number>(
  cartSelector,
  inCartSelector,
  (_, inCart: IState['inCart']) => {
    return inCart
      ? inCart.reduce(
          (accum, productInCart) =>
            productInCart.count * productInCart.product.price.discount + accum,
          0
        )
      : 0;
  }
)

createSelector<IRootState, IState, IState['inCart'], number>(
  cartSelector,
  inCartSelector,
  (_, inCart: IState['inCart']) => {
    return inCart
      ? inCart.reduce(
          (accum, productInCart) =>
            productInCart.count *
              (productInCart.product.price.value - productInCart.product.price.discount) +
            accum,
          0
        )
      : 0;
  }
)

